import{H as o}from"./Header-f92b1b8d.js";import{_ as k,o as d,c as l,b as e,a,d as n,r as t}from"./index-9268eda1.js";const u={name:"SejarahSekolahView",components:{Header:o}},m=a("div",{class:"container mx-auto px-4 md:max-w-default pt-6"},[a("div",{class:"block w-full p-6 bg-white border border-gray-200 rounded-lg shadow-sm dark:bg-gray-800 dark:border-gray-700"},[a("p",{class:"mt-1 text-base font-normal text-gray-700 dark:text-gray-400"},[n(" Tahun pelajaran 2004/2005 Kabupaten mendapatkan jatah 2 SMK yaitu SMK Negeri 2 dan SMK Negeri 3 Tabanan. Khusus untuk SMK Negeri 2 Tabanan, keberadaannya diperjuangkan oleh masyarakat desa adat Belayu. Dengan menyediakan tanah desa adat sebagai salah satu persyaratan kelayakan didirikan sekolah tersebut. "),a("br"),a("br"),n(" Diputuskan oleh masyarakat Belayu keberadaan SMK Negeri 2 Tabanan berlokasi di bekas tanah milik Yayasan Desa Adat (Dharma Bakti Belayu), maka sekolah tersebut berdiri di atas tanah seluas kurang lebih 32 are. Pada waktu itu yang menjabat Bupati Tabanan adalah I Nyoman Adi Wiryatama, S.Sos, Msi. dan sebagai Kepala Dinas Pendidikan Pemuda dan Olahraga adalah bapak Drs. I Wayan Adnyana, Msi. Kewenangan proses pendirian SMK Negeri 2 Tabanan tersebut menjadi hak dan wewenang Panitia Kabupaten Tabanan yang di ketuai oleh Drs. I Wayan Suela (senior SMK Negeri 1 Tabanan) bersama Kabid SMA/SMK pada waktu itu adalah Drs. I Gde Wayan Sukajana, termasuk panitia sudah mensetting siapa yang menjadi kepala sekolah di tempat itu. Pertimbangannya, karena sekolah tersebut dibangun di Desa Adat Belayu, maka oleh tokoh politik masyarakat Belayu (I Ketut Purnaya, S. Sos) mengusulkan Sdr. Drs. I Ketut Darmita Wirawan sebagai kepala sekolah pertama sejak tahun ajaran 2004/ 2005. Hal ini sedikit menimbulkan ketegangan antara pihak Panitia Kabupaten dengan tokoh politik masyarakat Belayu. "),a("br"),a("br"),n(" Sejak awal SMK Negeri 2 Tabanan oleh panitia menetapkan program keahlian Teknologi Informasi dan Komunikasi (Program keahlian Rekayasa Perangkat Lunak) sehingga lengkaplah keberadaan SMK Negeri di kabupaten Tabanan (SMKN 1 Tabanan = Bisnis, Managemen, dan Penjualan; SMKN 2 Tabanan = Teknologi informasi; dan SMKN 3 Tabanan = Otomotif dan Bangunan). Namun, oleh Bupati Tabanan SMK Negeri 2 Tabanan diharuskan membuka satu didang keahlian lagi, yakni: Tata Boga (Program Keahlian Restoran). Hal ini dimasudkan oleh Bupati agar putra-putra Tabanan (tamatan siswa) bisa berkompetisi untuk ke kapal pesiar, karena beliau setiap tahun memperoleh jatah pengiriman tenaga kerja ke kapal pesiar kurang lebih 100 orang dari rekannya pemilik Yayasan â€œBali Cruiseâ€. Maka, sejak tahun diklat itu pula SMK Negeri 2 Tabanan membuka 2 program keahlian, yakni : (1) Rekayasa Perangkat Lunak dan (2) Restoran. ")])])],-1),b=a("div",{class:"pb-10"},null,-1);function p(g,h,y,c,S,K){const i=t("Navbar"),r=t("Header"),s=t("Footer");return d(),l("main",null,[e(i),e(r,{title:"Sejarah Sekolah"}),m,b,e(s)])}const N=k(u,[["render",p]]);export{N as default};
